// Add a listener to track session changes with error handling
supabase.auth.onAuthStateChange((event, session) => {
  console.log('ğŸ” Supabase Auth State Change:', event, session ? 'Session exists' : 'No session');
  
  try {
    // Log session details for debugging
    if (session) {
      console.log('ğŸ” Session user:', session.user?.email);
      console.log('ğŸ” Session expires at:', new Date(session.expires_at! * 1000).toLocaleString());
      console.log('ğŸ” Session provider:', session.user?.app_metadata?.provider);
      
      // Check if session is about to expire (within 5 minutes)
      const expiresAt = new Date(session.expires_at! * 1000);
      const now = new Date();
      const fiveMinutes = 5 * 60 * 1000; // 5 minutes in milliseconds
      
      if (expiresAt.getTime() - now.getTime() < fiveMinutes) {
        console.warn('ğŸ” Session expires soon, triggering refresh...');
        supabase.auth.refreshSession();
      }
    }
    
    // Handle specific auth events
    if (event === 'TOKEN_REFRESHED') {
      console.log('ğŸ” Token refreshed successfully');
    } else if (event === 'SIGNED_OUT') {
      console.log('ğŸ” User signed out');
      // Clear any cached data
      localStorage.removeItem('educonnect-user-preferences');
    }
  } catch (error) {
    console.error('ğŸ” Error in auth state change handler:', error);
  }
});

// Handle OAuth callback
if (typeof window !== 'undefined') {
  // Check if we're on an OAuth callback page
  const urlParams = new URLSearchParams(window.location.search);
  const accessToken = urlParams.get('access_token');
  const refreshToken = urlParams.get('refresh_token');
  
  if (accessToken && refreshToken) {
    console.log('ğŸ” OAuth callback detected, setting session...');
    supabase.auth.setSession({
      access_token: accessToken,
      refresh_token: refreshToken,
    }).then(({ data, error }) => {
      if (error) {
        console.error('ğŸ” Error setting OAuth session:', error);
      } else {
        console.log('ğŸ” OAuth session set successfully:', data.session?.user?.email);
      }
    });
  }
}